package JavaFebPractice;

public class ArithmeticPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a= 20;
		int b= 8;
		
		a= a+1;
		b= b+1;
		
		a= a-1;
		b= b-1;
		
		System.out.println("a + b = " +(a+b));
		
		System.out.println("a - b = " +(a-b));
		
		System.out.println("a * b = " +(a*b));
		
		System.out.println("a / b = " +(a/b));
		
		System.out.println("a % b = " +(a%b));
		
		System.out.println(++a);
		
		System.out.println(b++);
		
		System.out.println(--a);
		
		System.out.println(b--);
		

	}

}
