package JavaFebPractice;

public class CompoundAssignmentPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=40;
		a+=8;
		System.out.println(a);
		a-=8;
		System.out.println(a);
		a*=8; 
		System.out.println(a);
		a/=8; 
		System.out.println(a);
		a%=8;
		System.out.println(a);
		

	}

}
